const should = require('should');
const SupplierModel = require('../index.js').model.supplier

const config = {
                    db: "se",
                    host: "127.0.0.1",
                    port: 27017
                }

var payload = {
        country: "uk",
        userName: "newuser5"
}

describe('SupplierModel Test', function() {

        it('Should return cpunts', function(done) {
                var Supplier = new SupplierModel(config, payload)

                Supplier.getScheduleCounts((err, result) => {
                    should(err).be.equal(null)
                    console.log(JSON.stringify(result))
                    done();
                })
        })

        it('Should return suppliers for the day index', function(done) {
                var Supplier = new SupplierModel(config, payload)

                Supplier.getScheduleDay(4, (err, result) => {
                    should(err).be.equal(null)
                    console.log(JSON.stringify(result))
                    done();
                })
        })

})
