const should = require('should');
const HtmlModel = require('../index.js').model.html
const config = {
                    db: "se",
                    host: "127.0.0.1",
                    port: 27017
                }

describe('Url Test', function() {

        it('should return cached html for the url', function(done) {
                var Html = new HtmlModel(config)

                Html.findByUrl("https://www.disneystore.com/home-decor/stationery-office/pens/mn/1026201/", (err, result) => {
                    should(err).be.equal(null)
                    result.should.have.property('total')
                    result.should.have.property('records')
                    result.total.should.be.above(0)
                    result.records.length.should.be.equal(1)
                    console.log(result)
                    done();
                })

        })

})
