const expect = require('chai').expect;
const JobModel = require('../model/job');

const config = {
    db: "sm",
    host: "localhost",
    port: 27017
};

describe('#JOB: Users Running Jobs: ', () => {

    let job = new JobModel(config);

    it('should return an array of Ids for people currently running jobs', (done) => {
        job.getUserIdWhereJobRunning((err, result) => {
            console.log('error: ', err);
            expect(err).to.equal(null);
            expect(result).to.be.an('array');
            expect(result.length).to.not.equal(0);
            console.log('result: ', result);
            done();
        });
    });
});
