const should = require('should');
const NotificationModel = require('../index.js').model.notification
const config = {
                    db: "se",
                    host: "127.0.0.1",
                    port: 27017
                }

describe('NotificationModel Test', function() {

        it('should return UK NotificationModel as array', function(done) {
                var Notificaiton = new NotificationModel(config)

                Notificaiton.insert({
                    userId: '1234',
                    type: 'SUCCESS',
                    message: 'A Lovely Message'
                }, (err, result) => {
                    should(err).be.equal(null)
                    done();
                })

        })

        it('should return notifications', function(done) {
            var Notification = new  NotificationModel(config)
            Notification.getAllNotifications((err, result)=> {
                should(err).be.equal(null)
                console.log(result);
                done()
            })

        })

})
