const should = require('should');
const ProductModel = require('../index.js').model.product
const payload = require('./data/saveAmazonDeltaTestData.js')
const config = require('../config').es;

describe('Save Amazon Delta Test', function() {

        this.timeout(10000)

        it('should update the Amzon Delta', function(done) {
                var Product = new ProductModel(config, 'us')

                Product.saveAmazonDelta(payload, (err, result) => {
                    should(err).be.equal(null)
                    done();
                })

        })

})
