const should = require('should');
const SearchRestructure = require('../model/searchRestructure');
const config = {
                    db: "se",
                    host: "127.0.0.1",
                    port: 27017
                }
const searchRecord = require('./searchRecord.js');

describe('Search Restructure Test', function() {

    it('should restructure the data', function() {
        var result = SearchRestructure(searchRecord)
        console.log(result);
        result.id.should.be.equal("5950df8e05dbbb4f423ad47c")
        result.name.should.be.equal("365games[BOT]_1_TO_60")
        result.domain.should.be.equal("www.365games.co.uk")
        result.status.should.be.equal("QUEUED")
        result.created.should.be.equal("2017-06-26T10:18:54.977Z")
        result.totalJobs.should.be.equal("10")
        result.completedJobs.should.be.equal("4")
        result.analysed.should.be.equal("234")
        result.categories[0].categoryName.should.be.equal("toys and games")
        result.categories[1].categoryName.should.be.equal("outdoors")
        result.categories[2].categoryName.should.be.equal("home and garden")
    })
})