const should = require('should');
const UserPortModel = require('../index.js').model.userport
const config = {
                    db: "se",
                    host: "127.0.0.1",
                    port: 27017
                }

describe('Port Test', function() {

        it('should return Ports as array', function(done) {
                var UserPort = new UserPortModel(config)

                UserPort.find({
                    size: 10
                }, (err, result) => {
                    should(err).be.equal(null)
                    result.should.have.property('total')
                    result.should.have.property('records')
                    result.total.should.be.above(0)
                    result.records.length.should.be.equal(10)
                    console.log(result)
                    done();
                })

        })

})
