var RemoveHtmlEntities = require('../lib/removeHtmlEntities.js')
module.exports = CleanName = function(name) {
    var result = null;
    if(name !== null) {
        result = RemoveHtmlEntities(name.replace('\r\n',' ').trim().replace(/&#xA0;/g,' ').replace(/\s\s+/g,' '));
    }
    return result;
}
