var md5 = require('md5')
var fs = require('fs')

module.exports = Cache= {
    load: function(key) {
        var md5Key = md5(key)
        var fileToGet = process.cwd() + '/downloadedPages/' + md5Key + '.html'
        try {
            return fs.readFileSync(fileToGet, 'utf-8')
        } catch(e) {
            return false
        }
    },
    save: function(key, data) {
        var md5Key = md5(key)
        var fileToSave = process.cwd() + '/downloadedPages/' + md5Key + '.html'
        fs.writeFile(fileToSave, data, function (err) {
            if (err) return console.log(err);
        });
    }
}
