module.exports = function inStockCheck(availability,instock) {
    if(availability !== undefined && availability !== null){
    if(availability !== '') {
        availability = availability.toLowerCase()
        if(availability.indexOf('out of stock') > -1 
        || availability.indexOf('pre-order') > -1 
        || availability.indexOf('not available') > -1 
        || availability.indexOf('sold out') > -1 
        || availability.indexOf('back in stock soon') > -1
        || availability.indexOf('pre order') > -1
        || availability.indexOf('no available') > -1
        || availability.indexOf('not in stock') > -1
        || availability.indexOf('unavailable') > -1
        || availability.indexOf('coming soon') > -1
        || availability.indexOf('no longer available') > -1
        || availability.indexOf('in stock soon') > -1
        || availability.indexOf('back in stock') > -1
        || availability.indexOf('notify me when item is available') > -1
        || availability.indexOf('expected to ship on') > -1
        || availability.indexOf('available in store only') > -1
        || availability.indexOf('available for order') > -1
        || availability.indexOf('limited availability') > -1
        || availability.indexOf('available online through marketplace sellers') > -1
        || availability.indexOf('available for purchase in store only') >-1
        || availability.indexOf('no longer carry this product') >-1 
        || availability.indexOf('can\'t seem to find that page right now') >-1
        || availability.indexOf('in-store only') > -1) {
            instock = false
            }
        }
    }
    return instock;
}
