const logger = require('x4-logger');
module.exports = loadSupplier = function(host) {

    var result;

    let logicFile = '../suppliers/' + host + '/logic.js'

    try {
        result =  require(logicFile)
    } catch(e) {
        if(e.code === 'MODULE_NOT_FOUND') {
            logger.error('NOT-IMPLEMENTED: ' + logicFile);
            logger.error(e)
        } else {
            logger.error(e)
        }
        result = false
    }

    return result;
}
