var same = function same(usage, argv) {
   var result = {}
   argv.map(function(item, index) {
      result[usage[index]] = item
   })
   return result
}

module.exports = Usage = function(usage) {
    if (usage.split(' ').length !== process.argv.length) {
        console.log('USAGE: ' + usage)
        process.exit()
    } else {
        return same(usage.split(' '), process.argv)
    }
}
