var Pagination = {

     items: function($) {
        var items = $('li.listFilter select').text();
        
        if(items !== ""){
            items = items.match(/[0-9]+/g).pop();
        }else {
          if(Search.container($).length > 0) {
                items = 0;
            }  
        }

        return parseInt(items);

     },
     pages: function($) {
        var items = $('li.listFilter select').text();
        
        if(items !== ""){
            items = items.match(/[0-9]+/g).pop();
        }else {
          if(Search.container($).length > 0) {
                items = 1;
            }  
        }

        var products = $('li.listCount ').text();
        var result = null;

        if(products !== ""){
           products = products.match(/[0-9]+/g).pop();
           result = Math.ceil(products / items);
        }else {
             if(Search.container($).length > 0) {
                result = 1;
            }
        }

        return result
     }
}

var Category = {
    name: function($) {
        var name = $('h1.catHeader.isL2Category').text();
        name = cleanName(name);
        return name;
    }
}
