var isStock = function inStock($) {
    var isInStock = true;
    var contentarea = $('.detailInfo').html()
    if(contentarea !== null) {
        if((contentarea.indexOf('Out of Stock') > -1)
        || (contentarea.indexOf('Unavailable') > -1))
        {
            isInStock = false
        }
    }
    return isInStock
}

var Price = function thePrice($, instock) {
    var otherPrice = $('.detailOurPrice').text()
    var price = $('.productprice').text()
    if(otherPrice !=''){
        price = getPrice(otherPrice)
    }else{
        price= getPrice(price)
    }
    //price = /[0-9]+.[0-9][0-9]/.exec(price)
    return price//[0]
}

var getName = function theName($, instock) {
    var name = $('h1').text()
    name = cleanName(name)
    return name
    

}

var image = function image($, instock) {
    var image = $('img.productMain').attr('src');
    return image;
}
var Category = {
    name: function($) {
        return cleanName($("div.detailPageTitle h1").text());
    }
}
var Pagination = {

     items: function($) {
        return Search.container($).length;

     },
     pages: function($) {
        return parseInt(cleanName($("div.detailPageTitle")
            .text()).match(/[0-9]+/g)[0] / this.items($));

     }
}
