function searchImage($, product) {
    return $(product).find('.picture img').attr('src');
}





var isStock = function inStock($) {
    var isInStock = true;
    var stockImage = $('#extra-buts').find('.imageStoreCollect').attr('src')
    var stock = $('.outofstock').text()
    isInStock = inStockCheck(stock,isInStock)
    if ((stockImage == "http://www.homebargains.co.uk/images/ccyeshdno.png")
    || (stockImage == "http://www.homebargains.co.uk/images/ccnohdno.png"))
    {
        isInStock = false
    }
    return isInStock
}

var getPrice = function thePrice($, instock) {
    var price = $('#productlt').find('.product-price').text().trim()
    price = /[0-9]+.[0-9][0-9]/.exec(price)
    if (price !== null){
        price = price[0].replace("£","").trim()
    }else{
        price = ""
    }
    return price
}

var getName = function theName($, instock) {
    return $('#rightheader3').text().trim()

}

var image = function image($, instock) {
    return $('a img#ctl00_ctl00_cph1_cph1_ctl00_ctrlProductInfo_defaultImage').attr('src');
}
var Category = {
    name: function($) {
        return cleanName($("a#ctl00_ctl00_cph1_cph1_ctl00_hlkName").text());
    }
}
var Pagination = {

     items: function($) {
        
        var match = cleanName($("select#ctl00_ctl00_" + 
            "cph1_cph1_ctl00_ddlPaging option").text()).match(/[0-9]+/g);

        if(match) {
           return parseInt(match[0]);
        } else {
            return null;
        }

     },
     pages: function($) {
        //return parseInt($("div#pagination").text().match(/[0-9]+/g).pop());

        return routine.matchNumbersAndPop($, "div#pagination");

     }
}