var isStock = function inStock($) {
    var isInStock = true;
    var stockcount = $('#stockCount').text()
    stockcount = /[0-9]+/.exec(stockcount)
    if (stockcount !== null)
    {
        if (stockcount[0] < 10)
        // This has been tested by increasing to 20 and swapping sufficient stock test to false
        {
            isInStock = false
        }
    }
    return isInStock
}

var getPrice = function thePrice($, instock) {
    return $('.itemPrice').text().replace("$","").trim()
}

var getName = function theName($, instock) {
    return $('h1').text().replace(/\n/g,"").replace(/\t/g,"").replace(/\r\n\r\n/g," ").replace(/\r\n/g," ").trim()

}

function image($, instock) {
    var image = $('img#productImage').attr('src');
    image = 'http:' + image;
    return image;
}

var Pagination = {

     items: function($) {
        var items = $('.product-view');
        items = items.length;
        return parseInt(items);

     },
     pages: function($) {
        var items = $('.product-view');
        items = items.length;

        var products = $('div#numberOfItemsDisplayed span').text();
        products = products.match(/[0-9]+/g).pop();
        

        sum = products / items
        return Math.ceil(sum)
     }
}

var Category = {
    name: function($) {
        var name = $('div#breadcrumb a').eq("-1").text();
        name = cleanName(name);
        return name;
    }
}
