var Product = {
    name: function ($) {
        var name = $('h1.product__hero__sidebar__title').html();
        name = cleanName(name);
        return name;

    },
    inStock: function ($) {
        return 'unchecked';

    },
    price: function ($) {
        var price = $('span.product__hero__sidebar__price__price').html().replace(/\n/g, "").replace("&#xA3", "").replace(";", "").replace(/\s/g, "");
        price = price + '.00';
        return price;

    },
    image: function ($) {
        var image = $('picture img').attr('src');
        (image);
        return image;

    }
}

var Search = {
    container: function ($) {
        var searchProduct = $('div.search-results__item');
        return searchProduct;

    },
    name: function ($, product) {
        var name = $(product).find('div.results__detail__title').html();
        name = cleanName(name);
        return name;

    },
    price: function ($, product) {
        var price = $(product).find('div.results__detail__price').html()
        price = getPrice(price)
        return price;

    },
    url: function ($, product) {
        var url = null;
        var anchor = $(product).find('a');
        if (anchor) {
            url = 'https://www.matalan.co.uk' + $(anchor).attr('href');
        }
        return url;


    },
    image: function ($, product) {
        var image = $(product).find('img').attr('src');
        return image;

    }
}
var Category = {
    name: function ($) {
        return cleanName($("h1.collection__intro__title").text());
    }
}
