function searchImage($, product) {
    return $(product).find('img').attr('src');
}

var isStock = function inStock($) {
    var isInStock = true;
    var producttop = $('#ShipDate').html();

    if (producttop !== null) {
        if ((producttop.indexOf('Out of Stock') > -1) ||
            (producttop.indexOf('Out of stock') > -1) ||
            (producttop.indexOf('Unavailable') > -1)) {
            isInStock = false
        }
    }
    return isInStock;
}

var getPrice = function thePrice($) {
    var price = $('.sale-price').text().trim().replace("$", "");
    price = /[0-9]+.[0-9][0-9]/.exec(price)
    if (price !== null) {
        price = price[0]
    }
    return price
}

var getName = function theName($) {
    return $('div.product-name').text().trim();
}

var image = function image($) {
    return $('img.img-responsive').attr('src');
}

var Pagination = {

    items: function ($) {
        var items = $('tr.b2b-product-info');

        if (items.length) {
            items = items.length;
            return parseInt(items);
        } else {
            return null;
        }


    },
    pages: function ($) {

        var pages = $('div.row div.pull-right span').text();
        var result = null;

        if (pages !== "") {
            pages = pages.match(/[0-9]+/g).pop();
            result = parseInt(pages);

            return result;

        } else {
            return null;
        }


    }
}

var Category = {
    name: function ($) {
        var name = $('div.hawk-top').text();
        name = cleanName(name);
        return name;
    }
}