var ProductPageInfo = require ('../../lib/productPageInfo.js');
var SearchPageInfo = require ('../../lib/searchPageInfo.js');
var PaginationNumberInfo = require  ('../../lib/paginationNumberInfo.js');
var cleanName = require('../../lib/cleanName.js');
var getPrice = require('../../lib/getPrice.js');
var CategoryInfo = require ('../../lib/CategoryInfo.js');

module.exports = replaceWithwebAddressChangeToCamelCase =  {
    "useJavaScript": true,
    'product' : function($, next) {
        // product - Returns product information for provided url
        var info  = new ProductPageInfo()
        info.name = Product.name($)
        info.inStock = Product.inStock($)
        info.price = Product.price($)
        info.image = Product.image($)
        info.country = 'us'
        info.description = Product.description($)

        return info
    },

    'search' : function($, next) {
        // search -  Returns an array of products from the suppliers search page
        var result = []

        Search.container($).map(function(index, product) {
            var info = new SearchPageInfo()
            info.name = Search.name($, product)
            info.price = Search.price($, product);
            info.url = Search.url($, product);
            info.image = Search.image($, product);
            info.country = 'us';
            result.push(info)
        });

        return result
    },

    'pagination' : function ($, next) {

        var info = new PaginationNumberInfo()
        info.items = Pagination.items($);
        info.pages = Pagination.pages($);

        return info
    },
    'category' : function($, next) {
        var info = new CategoryInfo()
        info.name = Category.name($);

        return info
    }
}

var Product = {
    name: function($) {
        var name = $('h1[itemprop="name"]').text();
        name = cleanName(name);

        return name;
    },
    inStock: function($) {
return 'unchecked'
},
    price: function($) {
        var price = $('span.singlePrice').text();
        price = getPrice(price);

        return price;
    },
    image: function($) {
        var image = $('div#pdpTop a').attr('href');
        image = 'http:' + image;

        return image;
    },
    description: function($) {
        var description = $('dl.descriptTabContent').text().replace(/\s+/g, " ").trim();

        return description;
    }
}

var Search = {
    container: function($) {
        var product = $('div.product-box');

        return product;
    },
    name: function($, product) {
        var brandName = $(product).find('span.brand-name').text();
        var itemName = $(product).find('span.item-name').text();

        var name = brandName + ' ' + itemName;
        name = cleanName(name);

        return name;
    },
    price: function($, product) {
        var price = $(product).find('span.base-price').text();
        
        if (price.indexOf(' - ') >- 1) {
            price = $(product).find('span.base-price').text().split(' - ').shift();
        } 

        price = getPrice(price);
        return price;
    },
    url: function($, product) {
        var url = $(product).find('a.product-box-link').attr('href').replace(':443', '');

        return url;
    },
    image: function($, product) {
        var image = $(product).find('a.product-box-link img').attr('src');

        return image;
    }
}

var Pagination = {

     items: function($) {
        var items = $('span.searched-stats').text();
        if(items !== "") {
            items = $('span.searched-stats').text().match(/[0-9]+/g)[1];
            items = JSON.parse(items);
        }else {
            if(Search.container($).length > 0) {
                result = 1;
            }
        }

            

        return items;
     },
     pages: function($) {
        var items = $('span.searched-stats').text();
        
        if(items !== "") {
            items = $('span.searched-stats').text().match(/[0-9]+/g)[1];
            items = JSON.parse(items);
        }else {
            if(Search.container($).length > 0) {
                result = 1;
            }
        }

        var result = null;
        var products = $('span.searched-stats').text();
        
        if(products !== "") {
            products = products.match(/[0,-9]+/g).pop().replace(',', '');
            result = Math.ceil(products / items);
        }else {
            if(Search.container($).length > 0) {
                result = 1;
            }
        }
        
        return result;
     }
}

var Category = {
    name: function($) {
        var name = $('ol.breadcrumb li').eq("-1").text();
        name = cleanName(name);
        return name;
    }
}

