var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://ao.com/product/240002-morphy-richards-aspect-toaster-black-36214-79.aspx"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Morphy Richards Aspect 240002 4 Slice Toaster - Black",
                    "price": "45.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://media.ao.com/en-GB/Productimages/Images/rvLarge/240002_bk_morphy_toaster_01_l.jpg',
                    "country": 'uk',
                    "sku": '36214'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://ao.com/l/kettles/1/55-106-78/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://ao.com/l/kettles/1/55-106-78/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Kettles"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://ao.com/l/toasters/1/55-106-79/"
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Russell Hobbs Legacy 4 Slice Polished 21303 4 Slice Toaster - Black';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '39.00',
                    url: 'http://www.ao.com/product/21303-russell-hobbs-legacy-4-slice-polished-toaster-black-35181-79.aspx',
                    image: 'http://media.ao.com/en-GB/Productimages/Images/rvMedium/russellhobbs_21303_bk_03_m_p.jpg',
                    country: 'uk',
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Bosch City Collection Long Slot TAT6805GB 2 Slice Toaster - Anthracite';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '62.00',
                    url: 'http://www.ao.com/product/tat6805gb-bosch-city-collection-long-slot-toaster-black-37749-79.aspx',
                    image: 'http://media.ao.com/en-GB/Productimages/Images/rvMedium/tat6805gb_an_bosch_toaster_01_m_p.jpg',
                    country: 'uk',
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
