var should = require('should');
var Structure = require('../lib/structure.js')

describe('b-gsales.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://b-gsales.com/ProductCategories/ArtsandCrafts/104708"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Duct Tape Circles 10 Yards x1.89",
                    "price": "1.89",
                    "url": "https://b-gsales.com/ProductCategories/ArtsandCrafts/104708",
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://b-gsales.com/Custom/FileSync/Product/104708/104708.jpg?height=300',
                    "country": 'us',
                    "sku": '104708'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://b-gsales.com/ProductCategories/Apparel?page=3"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 15,
                    "pages": 1
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://b-gsales.com/ProductCategories/ToysGames"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Toys & Games"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://b-gsales.com/ProductCategories/ArtsandCrafts"
        }

        // Change to be the number of products on the page
        var x = 15;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Waste Not Animal Chameleon Kit';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: "689196508448",
                    name: firstProductName,
                    price: '1.20',
                    url: 'https://b-gsales.com/ProductCategories/ArtsandCrafts/103256',
                    image: 'https://b-gsales.com/Custom/FileSync/Product/103256/103256.jpg?width=200&height=200',
                    country: 'us',
                    sku: '103256'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Doodle Rulers 12\" - 4AT Designs';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: '040232101861',
                    name: lastProductName,
                    price: '0.15',
                    url: 'https://b-gsales.com/ProductCategories/ArtsandCrafts/112660',
                    image: 'https://b-gsales.com/Custom/FileSync/Product/112660/112660.jpg?width=200&height=200',
                    country: 'us',
                    sku: '112660'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://b-gsales.com/"
        }

        it('should return null pages and items', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    });

});
