var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.beautybrands.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://bluemercury.com/collections/makeup/products/chantecaille-lip-chic",
            "html": cache.load("https://bluemercury.com/collections/makeup/products/chantecaille-lip-chic")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Chantecaille",
                    "price": "38.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/1265/7867/products/amaryllis-lip-chic-chantecaille-656509038046-amarylis-open_1024x1024.jpg?v=1537605671",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://bluemercury.com/collections/makeup/products/trish-mcevoy-essential-pencil",
            "html": cache.load("https://bluemercury.com/collections/makeup/products/trish-mcevoy-essential-pencil")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Trish McEvoy",
                    "price": "28.00",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/1265/7867/products/essential-pencil-trish-mcevoy-791222929919-nude-front_1024x1024.jpg?v=1537000287",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://bluemercury.com/collections/makeup/lips",
        "html": cache.load("https://bluemercury.com/collections/makeup/lips")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Lune+Aster';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '18.00',
                url: 'https://bluemercury.com/collections/makeup/products/lune-and-aster-vitamin-c-and-e-lip-gloss',
                image: 'https://cdn.shopify.com/s/files/1/1265/7867/products/bestie-ce-lip-gloss-luneaster-814309020269-closed_large.jpg?v=1535100558',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Trish McEvoy';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: null,
                url: 'https://bluemercury.com/collections/makeup/products/trish-mcevoy-essential-pencil',
                image: 'https://cdn.shopify.com/s/files/1/1265/7867/products/essential-pencil-trish-mcevoy-791222929919-nude-front_large.jpg?v=1537000287',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://bluemercury.com/collections/makeup/lips",
            "html": cache.load("https://bluemercury.com/collections/makeup/lips")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "lips",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
