var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.beautybrands.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://case-mate.com/black-waterfall-iphone-xs",
            "html": cache.load("https://case-mate.com/black-waterfall-iphone-xs")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Waterfall",
                    "price": "40",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://case-mate.com/media/catalog/product/cache/c687aa7517cf01e65c009f6943c2b1e9/c/m/cmi_pagani_waterfall_black_cm036264_1.png",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://case-mate.com/black-mclaren-iphone-xs",
            "html": cache.load("https://case-mate.com/black-mclaren-iphone-xs")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "McLaren",
                    "price": "50",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://case-mate.com/media/catalog/product/cache/c687aa7517cf01e65c009f6943c2b1e9/m/c/mclaren_1.png",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://case-mate.com/smartphone-cases/apple-cases/iphone-xs-cases",
        "html": cache.load("https://case-mate.com/smartphone-cases/apple-cases/iphone-xs-cases")
    }

    // Change to be the number of products on the page
    var x = 20;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Twinkle Stardust iPhone Xs / X';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '40',
                url: 'https://case-mate.com/twinkle-stardust-iphone-xs',
                image: 'https://case-mate.com/media/catalog/product/cache/d73a5018306142840707bd616a4ef293/t/w/twinkle_1_1.png',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Glow Waterfall Pink Glow iPhone Xs / X';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '40',
                url: 'https://case-mate.com/pink-glow-waterfall-iphone-xs',
                image: 'https://case-mate.com/media/catalog/product/cache/d73a5018306142840707bd616a4ef293/c/m/cm_waterfall_glowpink_icon.png',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://case-mate.com/smartphone-cases/apple-cases/iphone-xs-cases",
            "html": cache.load("https://case-mate.com/smartphone-cases/apple-cases/iphone-xs-cases")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "iPhone Xs / X",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
