var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://cpc.farnell.com/samsung/stshx-d601tdb/drive-external-usb3-0-d3-station/dp/CS28390"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "SAMSUNG STSHX-D601TDB D3 Station USB 3.0 External Hard Drive - 6TB",
                    "price": "164.34",
                    "url": 'http://cpc.farnell.com/samsung/stshx-d601tdb/drive-external-usb3-0-d3-station/dp/CS28390',
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://cpc.farnell.com/productimages/standard/en_GB/CS25395-40.jpg',
                    "country": 'uk',
                    "sku": 'CS28390'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://cpc.farnell.com/external-hard-drives"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 100,
                    "pages": 1
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://cpc.farnell.com/western-digital/wdbwlg0020hbk-uesn/drive-elements-desktop-2tb-wd/dp/CS27228"
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://cpc.farnell.com/external-hard-drives"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "External Hard Drives"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://cpc.farnell.com/external-hard-drives"
        }

        // Change to be the number of products on the page
        var x = 13;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'MAXTOR STSHX-D601TDBM D3 Station USB 3.0 External Hard Drive, 6TB';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '167.94',
                    url: 'http://cpc.farnell.com/maxtor/stshx-d601tdbm/drive-desktop-d3-station-6tb-maxtor/dp/CS30517',
                    image: 'http://cpc.farnell.com/productimages/promo/en_GB/CS29902-40.jpg',
                    country: 'uk',
                    sku: 'CS30517'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'FREECOM 56068 Quattro 3.0 3.5';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '146.66',
                    url: 'http://cpc.farnell.com/freecom/56068/drive-3-5-quattro-3-0-3tb-freecom/dp/CS23565',
                    image: 'http://cpc.farnell.com/productimages/promo/en_GB/CS22165-40.jpg',
                    country: 'uk',
                    sku: 'CS23565'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
