var should = require('should');
var Structure = require('../lib/structure.js')


describe('www.asda.co.uk', function() {

    before(function()  {
        this.timeout(5000);
    })


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://direct.asda.com/george/kids/wooden-toys/D25M8G1C17,default,sc.html#http://direct.asda.com/george/kids/wooden-toys/D25M8G1C17,default,sc.html?start=60&sz=20"
        }

        it('should return 72 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(72);
                done()
            })
        });

        it('last product should be a wooden fire truck', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'George Home Wooden Fire Truck',
                    price: '12.00',
                    url: 'http://direct.asda.com/george-home-wooden-fire-engine/000792145,default,pd.html',
                    image: 'http://asda.scene7.com/is/image/Asda/?layer=0&size=180,239&wid=180&hei=239&layer=1&resMode=sharp&size=180,239&src=is{Asda/5054070777046}&&defaultimage=default_listing_George_rd&qlt=75&fmt=pjpg&fmt=webp',
                    country: 'uk',
                    sku: '000792145'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be a George Home kitchen', function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'George Home Deluxe Kitchen',
                    price: '40.00',
                    url: 'http://direct.asda.com/george/kids/wooden-toys/george-home-deluxe-kitchen/000721481,default,pd.html',
                    image: 'http://asda.scene7.com/is/image/Asda/?layer=0&size=180,239&wid=180&hei=239&layer=1&resMode=sharp&size=180,239&src=is{Asda/5054781205081}&&defaultimage=default_listing_George_rd&qlt=75&fmt=pjpg&fmt=webp',
                    country: 'uk',
                    sku: '000721481'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://direct.asda.com/tefal-turbomix-hb1011a4-hand-blender/001585092,default,pd.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Tefal Turbomix HB1011A4 Hand Blender",
                    "price": "20.99",
                    "url": 'http://direct.asda.com/tefal-turbomix-hb1011a4-hand-blender/001585092,default,pd.html',
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://asda.scene7.com/is/image/Asda/3016661140150?hei=532&wid=910&qlt=85&fmt=pjpg&resmode=sharp&op_usm=1.1,0.5,0,0&defaultimage=default_details_George_rd",
                    "country": "uk",
                    "sku": '001585092'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Checking to see if no pagination works', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.walmart.com/"
        }

        it('should return 0/null items and null pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should return false when product is out of stock', function(done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "Breville VHB067 Hand Blender",
    //                 "price": "18.00",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     })

    // })

});
