var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.e-cosmetorium.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://e-cosmetorium.com/collections/make-up/products/dermacol-compact-powder?variant=37830010190",
            "html": cache.load("https://e-cosmetorium.com/collections/make-up/products/dermacol-compact-powder?variant=37830010190")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Dermacol Compact Powder",
                    "price": "6.50",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/0655/7851/products/PWR_01-small_grande.jpg?v=1490604168",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://e-cosmetorium.com/collections/make-up/products/refectocil-eyelash-perm-54-applications?variant=24425555459",
            "html": cache.load("https://e-cosmetorium.com/collections/make-up/products/refectocil-eyelash-perm-54-applications?variant=24425555459")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "RefectoCil Eyelash Perm 54 Applications",
                    "price": "56.23",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/0655/7851/products/RefectocilPerm54_grande.jpg?v=1470390512",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://e-cosmetorium.com/collections/make-up",
        "html": cache.load("https://e-cosmetorium.com/collections/make-up")
    }

    // Change to be the number of products on the page
    var x = 32;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Dermacol 24h Control Make-up (long lasting) 1 fl oz';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '10.31',
                url: 'https://e-cosmetorium.com/collections/make-up/products/dermacol-24h-control-make-up-long-lasting-1-fl-oz',
                image: 'https://cdn.shopify.com/s/files/1/0655/7851/products/Dermacol24hControlMain_large.jpg?v=1469525692',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'RefectoCil Skin Protection Cream 2.5 fl oz';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '11.80',
                url: 'https://e-cosmetorium.com/collections/make-up/products/refectocil-skin-protection-cream-2-5-fl-oz',
                image: 'https://cdn.shopify.com/s/files/1/0655/7851/products/RefectocilSkinProtectionCream_large.jpg?v=1486551465',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://e-cosmetorium.com/collections/make-up",
            "html": cache.load("https://e-cosmetorium.com/collections/make-up")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Make-Up",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
