    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://electrical.coop.co.uk/computers-gaming/computing/laptops/"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Laptops"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://electrical.coop.co.uk/computers-gaming/computing/laptops/"
        }

        // Change to be the number of products on the page
        var x = 11;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'HP Envy 15AQ100NA Laptop 15.6\" FHD Touch Core i5-7200U 8GB D...';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '849.00',
                    url: 'https://electrical.coop.co.uk/brands/hewlett-packard/hp-envy-15aq100na-laptop-15-6-fhd-touch-core-i5-7200u-8gb-ddr4-1tb-sata-128gb-hew-lap-15aq100na-ns/',
                    image: 'https://electrical.coop.co.uk/globalassets/upload/hew-lap-15aq100na-ns.png?width=200&quality=70&format=jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'HP 17X042NA Laptop 17.3" HD Core i3-6006U, 8GB, 2TB SATA';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '499.00',
                    url: 'https://electrical.coop.co.uk/brands/hewlett-packard/hp-17x042na-laptop-17-3-hd-core-i3-6006u-8gb-2tb-sata-hew-lap-17x042na-s/',
                    image: 'https://electrical.coop.co.uk/globalassets/upload/hew-lap-17x042na-s.png?width=200&quality=70&format=jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

