var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.energenie4u.co.uk', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://energenie4u.co.uk/catalogue/product/MIHO075",
            "html": cache.load("https://energenie4u.co.uk/catalogue/product/MIHO075")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "MiHome Dimmer Black Nickel",
                    "price": "49.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://energenie4u.co.uk/res/images/products/large/MIHO024 WEBSITE.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://energenie4u.co.uk/catalogue/product/ENER103",
            "html": cache.load("https://energenie4u.co.uk/catalogue/product/ENER103")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Rechargeable AA-size Batteries",
                    "price": "10.95",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://energenie4u.co.uk/res/images/products/large/ENER103_a.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://energenie4u.co.uk/catalogue/category/Home-Automation",
        "html": cache.load("https://energenie4u.co.uk/catalogue/category/Home-Automation")
    }

    // Change to be the number of products on the page
    var x = 58;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Mi|Home Gateway';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '69.99',
                url: 'https://energenie4u.co.uk/catalogue/product/MIHO001',
                image: 'https://energenie4u.co.uk/res/images/products/medium/MIHO001 WEBSITE.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Mi|Home Whole House Monitor';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '39.99',
                url: 'https://energenie4u.co.uk/catalogue/product/MIHO006',
                image: 'https://energenie4u.co.uk/res/images/products/medium/MIHO006 WEBSITE.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://energenie4u.co.uk/catalogue/category/Home-Automation",
            "html": cache.load("https://energenie4u.co.uk/catalogue/category/Home-Automation")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Home Automation",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
