var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('familyotc.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://familyotc.com/collections/skin-care/products/vanicream-gentle-facial-cleanser-for-sensitive-skin-8-oz",
            "html": cache.load("https://familyotc.com/collections/skin-care/products/vanicream-gentle-facial-cleanser-for-sensitive-skin-8-oz")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Vanicream Gentle Facial Cleanser for Sensitive Skin 8 oz",
                    "price": "6.97",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/0013/2408/9380/products/C4950853-M2710911-2_2000x.jpg?v=1523364529",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://familyotc.com/collections/skin-care/products/wibi-dry-skin-lotion-16oz",
            "html": cache.load("https://familyotc.com/collections/skin-care/products/wibi-dry-skin-lotion-16oz")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Wibi Dry Skin Lotion - 16oz",
                    "price": null,
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/0013/2408/9380/products/51Me4gvZRmL._SX522_2000x.jpg?v=1527645404",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://familyotc.com/collections/skin-care",
        "html": cache.load("https://familyotc.com/collections/skin-care")
    }

    // Change to be the number of products on the page
    var x = 32;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'DermaZinc Cream - 4 oz';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '16.97',
                url: 'https://familyotc.com/collections/skin-care/products/dermazinc-cream-4-oz',
                image: 'https://cdn.shopify.com/s/files/1/0013/2408/9380/products/CD3271475-2_900x.jpg?v=1523313511',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Vanicream Moisturizing Skin Cream - 4 oz';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '5.57',
                url: 'https://familyotc.com/collections/skin-care/products/vanicream-moisturizing-skin-cream-4-oz',
                image: 'https://cdn.shopify.com/s/files/1/0013/2408/9380/products/C2048882-M3693041-2_100x.jpg?v=1523364533',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://familyotc.com/collections/skin-care",
            "html": cache.load("https://familyotc.com/collections/skin-care")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Skin Care",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
