var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('www.fisher-price.com', function() {

    this.timeout(5000);

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://fisher-price.mattel.com/shop/en-us/fp/toys-for-2-year-olds#facet:&productBeginIndex:0&orderBy:&pageView:grid&minPrice:&maxPrice:&pageSize:&contentPageSize:&",
            "html": cache.load("http://fisher-price.mattel.com/shop/en-us/fp/toys-for-2-year-olds#facet:&productBeginIndex:0&orderBy:&pageView:grid&minPrice:&maxPrice:&pageSize:&contentPageSize:&")
        }

        it('should return 36 products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(36);
                done()
            })
        });

        it('last product should be Newborn-to-Toddler Portable Rocker', function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Mega Bloks® Thomas™ Build & Go™',
                    price: '35.00',
                    url: 'http://fisher-price.mattel.com/shop/en-us/mattel/toys-for-2-year-olds/mega-bloks-thomas-build-go-ffd63',
                    image: 'http://images.mattel.com/scene7//wcsstore/MattelCAS/FFD63_01?$ossmallindex$&storeId=10151&SKU=FFD63',
                    country: 'us',
                    sku: 'mega-bloks-thomas-build-go-ffd63'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be Disney Princess Magical Wand Palace by Little People®', function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: "Disney Princess Magical Wand Palace by Little People®",
                    price: '50.00',
                    url: 'http://fisher-price.mattel.com/shop/en-us/mattel/toys-for-2-year-olds/disney-princess-magical-wand-palace-by-little-people-drl52',
                    image: 'http://images.mattel.com/scene7//wcsstore/MattelCAS/DRL52_01?$ossmallindex$&storeId=10151&SKU=DRL52',
                    country: 'us',
                    sku: 'disney-princess-magical-wand-palace-by-little-people-drl52'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://fisher-price.mattel.com/shop/en-us/fp/newborn-baby-toys-gear#facet:&productBeginIndex:0&orderBy:&pageView:grid&minPrice:&maxPrice:&pageSize:&contentPageSize:&",
            "html": cache.load("https://fisher-price.mattel.com/shop/en-us/fp/newborn-baby-toys-gear#facet:&productBeginIndex:0&orderBy:&pageView:grid&minPrice:&maxPrice:&pageSize:&contentPageSize:&")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 36,
                    "pages": 6,
                    "url": payload.url
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });


    // describe('Search page pagination', function() {

    //     var payload = {
    //         "pageType" : "pagination",
    //         "url" : "http://fisher-price.mattel.com/shop/en-us/fp/whats-new/think-learn-smart-scan-color-chameleon#productBeginIndex:0",
    //         "html": cache.load("http://fisher-price.mattel.com/shop/en-us/fp/whats-new/think-learn-smart-scan-color-chameleon#productBeginIndex:0")
    //     }

    //     it('should return 0 and 1 if no pagination occurs', function (done) {

    //         Structure(payload, function(err, pagination) {
    //             var expected = JSON.stringify({
    //                 "items": 1,
    //                 "pages": 1,
    //                 "url": payload.url
    //             })
    //             JSON.stringify(pagination).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://fisher-price.mattel.com/shop/en-us/fp/search/playsets-action-figures#productBeginIndex:72",
            "html": cache.load("http://fisher-price.mattel.com/shop/en-us/fp/search/playsets-action-figures#productBeginIndex:72")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Playsets & Action Figures",
                    "url" : payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

  

    describe('In Stock Page', function() {

        var firstProductName = 'Laugh & Learn® Servin Up Fun Food Truck';
        it('first product should be ' + firstProductName, function (done) {

        var payload = {
            "pageType" : "product",
            "url" : "https://fisher-price.mattel.com/shop/en-us/fp/toys-for-1-year-olds/laugh-learn-servin-up-fun-food-truck-dym74",
            "html": cache.load("https://fisher-price.mattel.com/shop/en-us/fp/toys-for-1-year-olds/laugh-learn-servin-up-fun-food-truck-dym74")
        }
        
        Structure(payload, function (err, result) {
                
                should(err).be.equal(null)
                    result.name.should.be.equal("Laugh & Learn® Servin' Up Fun Food Truck")
                    result.price.should.be.equal('69.99')
                    result.url.should.be.equal("https://fisher-price.mattel.com/shop/en-us/fp/toys-for-1-year-olds/laugh-learn-servin-up-fun-food-truck-dym74")
                    result.inStock.should.be.equal(true)
                    result.image.should.be.equal("http://images.mattel.com/scene7/DYM74_01?$oslarge$")
                    result.country.should.be.equal("us")
                    done()
                });
        });

    });


    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://fisher-price.mattel.com/shop/en-us/fp/shop-by-category/rock-stack-babys-first-blocks-bundle-fwp37",
            "html": cache.load("https://fisher-price.mattel.com/shop/en-us/fp/shop-by-category/rock-stack-babys-first-blocks-bundle-fwp37")
        }

        var lastProductName = 'Rock-A-Stack and Babys First Blocks Bundle';
        it('last product should be' + lastProductName, function(done) {

        Structure(payload, function (err, result) {
               
                should(err).be.equal(null)
                    result.name.should.be.equal("Rock-A-Stack and Baby's First Blocks Bundle")
                    result.price.should.be.equal('15.00')
                    result.url.should.be.equal("https://fisher-price.mattel.com/shop/en-us/fp/shop-by-category/rock-stack-babys-first-blocks-bundle-fwp37")
                    result.inStock.should.be.equal(false)
                    result.image.should.be.equal("http://images.mattel.com/scene7/FWP37_01?$oslarge$")
                    result.country.should.be.equal("us")
                    done()
            })
        })

    })

});
