    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://jet.com/c/electronics/computers-and-accessories/laptops/10000086",
            "html": cache.load('https://jet.com/c/electronics/computers-and-accessories/laptops/10000086')
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });


        var firstProductName = 'Apple MacBook Pro (13-inch) with Touch Bar';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1799.00',
                    url: 'https://jet.com/product/Apple-MacBook-Pro-13-inch-with-Touch-Bar/144adaf2594a4634bbb97366059774d8?beaconId=49cad925-4bf1-4f47-a98e-17635f35434c%2F1%2Fx~144adaf2594a4634bbb97366059774d8&origination=PLP',
                    image: 'https://jetimages.jetcdn.net/md5/fb6651d510f3071c32c33ffe8c2116ca.500',
                    country: 'us',
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })


        var lastProductName = 'HP 15-bw002wm 15.6" Marine Blue Laptop, Windows 10, AMD E2-9000e Processor, 4GB Memory, 500GB Hard Drive, DVD, with Wireless Mouse and Sleeve Included';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: null,
                    url: 'https://jet.com/product/HP-15-bw002wm-156-Marine-Blue-Laptop-Windows-10-AMD-E2-9000e-Processor-4GB-Memor/467995c88e3643bdb1af6e1cc222b4e7?beaconId=49cad925-4bf1-4f47-a98e-17635f35434c%2F24%2Fx~467995c88e3643bdb1af6e1cc222b4e7&origination=PLP',
                    image: 'https://jetimages.jetcdn.net/md5/85552aeb31f365b765b6a06ba633b155.500',
                    country: 'us',
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

