    var should = require('should');
    var Structure = require('../lib/structure.js')
    const cache = require('../lib/cache')

    describe('Keepa Categories', function () {

        describe('Keepa Categories', function () {

            var payload = {
                "pageType": "search",
                "url": "https://keepa.com/#!categorytree",
                "html": cache.load("https://keepa.com/#!categorytree")
            }
            var x = 27
            it('should return' + x + ' number of categories', function () {

                const searchPage = Structure(payload)

                keepaCategoryData = {}

                searchPage.map((data) => {
                    let category = data.category
                    let childCount = data.childCount
                    let numberOfProducts = data.categoryCount
                    let country = data.country
                    let url = data.url

                    keepaCategoryData[category] = {
                        childCount: childCount,
                        numberOfProducts: numberOfProducts,
                        country: country,
                        url: url
                    }

                })
                console.log(keepaCategoryData)
            });
        });
    });