var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('marystack.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://marystack.com/cavalleria-toscana-regular-riding-jacket/",
            "html": cache.load("https://marystack.com/cavalleria-toscana-regular-riding-jacket/")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Cavalleria Toscana Regular Riding Jacket",
                    "price": "690.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn7.bigcommerce.com/s-99vj2qx/images/stencil/500x659/products/19477/60379/cavalleria-jacket-tree__59985.1529958264.jpg?c=2&imbypass=on",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://marystack.com/myler-comfort-snaffle-dee-with-hooks-mb-01-level-1/",
            "html": cache.load("https://marystack.com/myler-comfort-snaffle-dee-with-hooks-mb-01-level-1/")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Myler Comfort Snaffle Dee with Hooks - MB 01 - Level 1",
                    "price": "109.95",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://cdn7.bigcommerce.com/s-99vj2qx/images/stencil/500x659/products/10658/45730/myler-dee-ring-with-hooks-comfort-snaffle-mb-01-2__09543__83726.1526501004.jpg?c=2&imbypass=on",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://marystack.com/for-the-rider/english-riding-apparel/english-show-coats/hunter-jumper-show-coats/",
        "html": cache.load("https://marystack.com/for-the-rider/english-riding-apparel/english-show-coats/hunter-jumper-show-coats/")
    }

    // Change to be the number of products on the page
    var x = 12;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'RJ Classics Nora Coat';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '239.00',
                url: 'https://marystack.com/rj-classics-nora-coat/',
                image: 'https://cdn7.bigcommerce.com/s-99vj2qx/images/stencil/500x659/products/19770/61933/rj-classics-nora-coat-grey__84197.1535477109.jpg?c=2',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Samshield Alix Jacket';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '689.00',
                url: 'https://marystack.com/samshield-alix-jacket/',
                image: 'https://cdn7.bigcommerce.com/s-99vj2qx/images/stencil/500x659/products/19167/59664/samshield-alix-jacket-navy-front__63346__32912.1528327520.jpg?c=2',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://marystack.com/for-the-rider/english-riding-apparel/english-show-coats/hunter-jumper-show-coats/",
            "html": cache.load("https://marystack.com/for-the-rider/english-riding-apparel/english-show-coats/hunter-jumper-show-coats/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Hunter / Jumper Show Coats",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
