var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://mbeans.com/austlen-baby-entourage-stroller.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "austlen baby entourage stroller",
                    "price": "849.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://mbeans.com/images/items/main/_auto/000/0F4/8F/EntourageExpandedBlack_Austlen.jpg',
                    "country": 'us',
                    "description": 'Having a child changes a lot of things, but it doesn’t need to change everything. A picnic in the park might sound like an unpredictable and challenging outing – intimidating enough to keep everyone home. And taking the family on a trip? That can feel more like work than play! Austlen™ Baby Co. believes that baby gear should allow parents the ability to go and do, and that’s what our products have been designed for – to give parents more freedom to live in the moment. So go shopping. Bring the picnic. Travel anywhere. Austlen Baby Co. Get your parenting in gear™. Features: Three linear stages allow you to switch as you go with easy, one-handed expansion Included Market tote and lower storage area expand with stroller to carry extra-large items Seat has five recline positions and an adjustable calf support to accommodate children from birth to 50 lbs. Compact, self-standing fold possible without removing secondary seating Fashionable fabrics and finishes offer style, quality, and durability Included leatherette belly bar Leatherette, height-adjustable handlebar Removable and washable seat fabrics Generous canopy with sun visor One-piece, self-stand fold (even with two seats!) Shock-absorbing dual suspension Never-flat tires Striped seat liner sold separately'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://mbeans.com/baby"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 120,
                    "pages": 8
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://mbeans.com/baby"
        }

        // Change to be the number of products on the page
        var x = 120;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Baby Ktan Baby Carrier';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '49.99',
                    url: 'http://mbeans.com/baby-ktan-baby-carrier.html',
                    image: null,
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Baby Jogger City Tour Cup Holder 2016/2017';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '14.95',
                    url: 'http://mbeans.com/baby-jogger-city-tour-cup-holder-2016-2017.html',
                    image: null,
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://mbeans.com/baby"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Magic Beans Search"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
