var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://palmettostatearmory.com/hsp-multi-mission-hanger-cb-mmhc.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "HSP Multi-Mission Hanger CB--MMHC",
                    "price": "44.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://palmettostatearmory.com/media/catalog/product/cache/1/image/370x370/9df78eab33525d08d6e5fb8d27136e95/e/x/expanded_d3cr_mc_1.jpg',
                    "country": 'us',
                    "barcode": '131300001384'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://palmettostatearmory.com/safariland-concealment-magazine-paddle-holder-model-76-132.html"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Safariland Model 074 Open Top Single Magazine Pouch - LH- 074-76-132",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://palmettostatearmory.com/media/catalog/product/cache/1/image/370x370/9df78eab33525d08d6e5fb8d27136e95/1/5/1573.jpg",
                    "country": "us",
                    "barcode": "781602616164"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://palmettostatearmory.com/ak-47.html"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 30,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://palmettostatearmory.com/accessories/molle-accessories.html"
        }

        // Change to be the number of products on the page
        var x = 11;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'HSP Multi-Mission Hanger CB--MMHC';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '44.99',
                    url: 'http://palmettostatearmory.com/hsp-multi-mission-hanger-cb-mmhc.html',
                    image: 'http://palmettostatearmory.com/media/catalog/product/cache/1/small_image/160x/9df78eab33525d08d6e5fb8d27136e95/h/a/hanger_mc_1.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'TAC Shield Tactical 8 Hour Light Sticks (10 Pack)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '14.99',
                    url: 'http://palmettostatearmory.com/tac-shield-tactical-8-hour-light-sticks-10-pack.html',
                    image: 'http://palmettostatearmory.com/media/catalog/product/cache/1/small_image/160x/9df78eab33525d08d6e5fb8d27136e95/t/a/tac-shield-light-stick---blue_1.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://palmettostatearmory.com/accessories/molle-accessories.html"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Tactical Gear"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
