var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://shop.crayola.com/toys-and-activities/trains/g-gauge-lionel-train-set-8203200000.html",
            "html" : cache.load("http://shop.crayola.com/toys-and-activities/trains/g-gauge-lionel-train-set-8203200000.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "G-Gauge Lionel Train Set",
                    "price": "87.99",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'http://cdn-eu-ec.yottaa.net/5717c375312e5835ae000dc4/shop.crayola.com/v~13.1a/dw/image/v2/AALB_PRD/on/demandware.static/-/Sites-crayola-storefront/default/dw16d4ef24/images/711548.Drama.jpg?sw=390&sh=350&sm=fit&sfrm=jpg&yocs=m_&yoloc=eu',
                    "country": 'us',
                    "sku":"8203200000"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://shop.crayola.com/toys-and-activities",
            "html" : cache.load("http://shop.crayola.com/toys-and-activities")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 2,
                    "url":"http://shop.crayola.com/toys-and-activities"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://shop.crayola.com/toys-and-activities",
            "html" : cache.load("http://shop.crayola.com/toys-and-activities")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Toys and Activities",
                    "url":"http://shop.crayola.com/toys-and-activities"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://shop.crayola.com/paint-and-create/brushes",
            "html" : cache.load("http://shop.crayola.com/paint-and-create/brushes")
        }

        // Change to be the number of products on the page
        var x = 10;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'My First Art Smock';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '4.99',
                    url: 'http://shop.crayola.com/paint-and-create/scissors-and-glue/my-first-art-smock-8113790000.html',
                    image: 'http://cdn-eu-ec.yottaa.net/5717c375312e5835ae000dc4/shop.crayola.com/v~22.25/dw/image/v2/AALB_PRD/on/demandware.static/-/Sites-crayola-storefront/default/dw7d4cf078/images/81-1379-0-150_My-First_Art-Smock_F1.jpg?sw=357&sh=323&sm=fit&sfrm=jpg&yocs=m_&yoloc=eu',
                    country: 'us',
                    sku:"8113790000"
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = '36 ct. Large Variety Paint Brushes Classpack';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '59.99',
                    url: 'http://shop.crayola.com/hidden/crayons-and-paints/36-ct.-large-variety-paint-brushes-classpack-0500360000.html',
                    image: 'http://cdn-eu-ec.yottaa.net/5717c375312e5835ae000dc4/shop.crayola.com/v~22.25/dw/image/v2/AALB_PRD/on/demandware.static/-/Sites-crayola-storefront/default/dwb674f93d/images/05-0036-0-200_Classpack_Paintbrush_Variety_36ct_H1.jpg?sw=357&sh=323&sm=fit&sfrm=jpg&yocs=m_&yoloc=eu',
                    country: 'us',
                    sku:"0500360000"
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
