var should = require('should');
var Structure = require('../lib/structure.js')

describe('shop.mattel.com', function() {

    before(function()  {
        this.timeout(5000);
    })

     describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://shop.mattel.com/shop/en-us/ms/games#facet:&productBeginIndex:0&orderBy:&pageView:grid&minPrice:&maxPrice:&pageSize:&contentPageSize:&"
        }

        it('should return 36 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(36);
                done()
            })
        });

        it('last product should be Hydroshield Water Dodger', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Hydroshield Water Dodger',
                    price: '24.99',
                    url: 'http://shop.mattel.com/shop/en-us/ms/games/hydroshield-water-dodger-fwt67',
                    image: 'http://images.mattel.com/scene7//wcsstore/MattelCAS/FWT67_01?$ossmallindex$&storeId=10151&SKU=FWT67',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be Bloxels™ Video Game Builder', function(done) {

            Structure(payload, function(result) {

                 var first = result.shift()
                    var expected = JSON.stringify({
                        barcode: null,
                        name: 'Bloxels™ Video Game Builder',
                        price: '49.99',
                        url: 'http://shop.mattel.com/shop/en-us/ms/games/bloxels-video-game-builder-ffb15',
                        image: 'http://images.mattel.com/scene7//wcsstore/MattelCAS/FFB15_01?$ossmallindex$&storeId=10151&SKU=FFB15',
                        country: 'us'
                    })

                    JSON.stringify(first).should.equal(expected)
                    done()
                })

        })


    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://shop.mattel.com/shop/en-us/ms/action-figures#productBeginIndex:0"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": "36",
                    "pages": "9"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Not a real search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://shop.mattel.com/shop/en-us/ms/dc-universe-toys/dc-comics-steel-figure-cdx28"
        }

        it('should be true when the items and pages are null', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });


    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://shop.mattel.com/shop/en-us/ms/search/hot-wheels#facet:&productBeginIndex:0&orderBy:&pageView:&minPrice:&maxPrice:&pageSize:&contentPageSize:&"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Hot Wheels®"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://shop.mattel.com/product/index.jsp?productId=90226076"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    name: 'Batman™ V Superman™ 6-Inch Tall Gauntlet Assault™ Batman™ Figure',
                    price: '9.99',
                    url: null,
                    inStock: true,
                    stockLevel: null,
                    image: 'http://mat.imageg.net/graphics/product_images/pMAT1-23805743v380.jpg',
                    country: 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://shop.mattel.com/shop/en-us/ms/barbie-fantasy-dolls/barbie-groom-doll-dhc36"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    name: 'Barbie® Groom Doll',
                    price: '8.99',
                    url: null,
                    inStock: false,
                    stockLevel: null,
                    country: 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })


 });


