var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://shop.riteaid.com/ensure-complete-nutrition-shake-vanilla-6-8-fl-oz-237-ml-bottles-0039128"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Ensure Complete Nutrition Shake, Vanilla 6 - 8 fl oz (237 ml) bottles",
                    "price": "9.49",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://dl4c2alc64h05.cloudfront.net/media/catalog/product/cache/1/image/290x290/17f82f742ffe127f42dca9de82fb58b1/0/0/0039128.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://shop.riteaid.com/vitamins-supplements?limit=72"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 72,
                    "pages": 17
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://shop.riteaid.com/vitamins-supplements?limit=72"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Vitamins & Supplements"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url": "https://shop.riteaid.com/vitamins-supplements"
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Ensure Complete Nutrition Shake, Vanilla 6 - 8 fl oz (237 ml) bottles';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '9.49',
                    url: 'https://shop.riteaid.com/ensure-complete-nutrition-shake-vanilla-6-8-fl-oz-237-ml-bottles-0039128',
                    image: 'https://shop.riteaid.com/media/catalog/product/cache/1/small_image/220x/9df78eab33525d08d6e5fb8d27136e95/0/0/0039128.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Rite Aid Pharmacy Magnesium Citrate Oral Solution, 1.745 g, Lemon Flavor, 10 fl oz (296 ml)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '2.99',
                    url: 'https://shop.riteaid.com/rite-aid-pharmacy-magnesium-citrate-oral-solution-1-745-g-lemon-flavor-10-fl-oz-296-ml-0033300',
                    image: 'https://shop.riteaid.com/media/catalog/product/cache/1/small_image/220x/9df78eab33525d08d6e5fb8d27136e95/0/0/0033300.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
