var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://store.approvedfood.co.uk/health_foods/the_organic_protein_company_organic_whey_protein_400g-205088-p?pid=205088"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "The Organic Protein Company Organic Whey Protein 400g",
                    "price": "9.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://fthumb.approvedfood.co.uk/thumbs/75/814/1000/1/src_images/Organic_Whey_Protein___400g.jpg',
                    "country": 'uk',
                    "barcode": '702403240297'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://store.approvedfood.co.uk/sale"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 25,
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://store.approvedfood.co.uk/body_care/synergy_health_synergy_health_assure_antibacterial_hand_mousse_50ml-189326-p?pid=189326"
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://store.approvedfood.co.uk/search?terms=protein&quick=1&"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "protein"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://store.approvedfood.co.uk/search?terms=protein&quick=1&"
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'The Organic Protein Company Organic Whey Protein 400g';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '9.99',
                    url: 'http://store.approvedfood.co.uk/health_foods/the_organic_protein_company_organic_whey_protein_400g-205088-p?pid=205088',
                    image: 'http://fthumb.approvedfood.co.uk/thumbs/75/93/115/2/src_images/Organic_Whey_Protein___400g.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'SMA Wysoy Soya Infant Formula From Birth 860g';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '12.95',
                    url: 'http://store.approvedfood.co.uk/baby_care/sma_wysoy_soya_infant_formula_from_birth_860g-185258-p?pid=185258',
                    image: 'http://fthumb.approvedfood.co.uk/thumbs/75/115/115/2/src_images/SMA_Wysoy_Soya_Infant_Formula_From_Birth_860g.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
