    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://store.starbucks.com/equipment/coffee-makers/"
        }

        // Change to be the number of products on the page
        var x = 22;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Verismo® System by Starbucks";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '179.00',
                    url: 'http://store.starbucks.com/products/verismo-system-by-starbucks-BNDLVERISMOSYSTEMUS',
                    image: 'http://demandware.edgesuite.net/sits_pod26/dw/image/v2/AAFV_PRD/on/demandware.static/-/Sites-starbucks-master-catalog/default/dwb4141d88/images/api-images/GR/verismo_system_us_ca_GR.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Nuova Simonelli® Oscar Professional';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '1295.00',
                    url: 'http://store.starbucks.com/products/nuova-simonelli-oscar-professional-011035539',
                    image: 'http://demandware.edgesuite.net/sits_pod26/dw/image/v2/AAFV_PRD/on/demandware.static/-/Sites-starbucks-master-catalog/default/dw7838a4ab/images/api-images/GR/nova_simonelli_oskar_black_GR.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://store.starbucks.com/coffee/whole-bean-and-ground"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 25,
                    "pages": 1
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://store.starbucks.com/tea/green-tea"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Green Tea"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


