var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://uk.bodybuilding.com/store/opt/whey.html?skuId=OPTEU1360046",
            "html": cache.load("https://uk.bodybuilding.com/store/opt/whey.html?skuId=OPTEU1360046")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Gold Standard 100% Whey",
                    "price": "25.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://artifacts.bbcomcdn.com/bb-resources/2.6.0/flags/4x3/gb.svg',
                    "country": 'uk',
                    "description": '',
                    "sku":"OPTEU1360046"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://uk.bodybuilding.com/store/fitmiss/ignite.html?_requestid=107176",
            "html": cache.load("http://uk.bodybuilding.com/store/fitmiss/ignite.html?_requestid=107176")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "FitMiss:Ignite",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://d.adroll.com/cm/aol/out',
                    "country": 'uk',
                    "description": 'Supports Energy & Workout Motivation!*',
                    "sku":"107176"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://uk.bodybuilding.com/store/whey.html?_requestid=107341",
            "html": cache.load("http://uk.bodybuilding.com/store/whey.html?_requestid=107341")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 20,
                    "pages": 24,
                    "url" : "http://uk.bodybuilding.com/store/whey.html?_requestid=107341"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })


    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://uk.bodybuilding.com/store/whey.html?_requestid=107341",
            "html": cache.load("http://uk.bodybuilding.com/store/whey.html?_requestid=107341")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "All Whey Protein Products",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });



    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://uk.bodybuilding.com/store/whey.html",
            "html": cache.load("https://uk.bodybuilding.com/store/whey.html")
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Optimum Nutrition Gold Standard 100% Whey,Â 2.2 Kilograms";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err,result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '47.99',
                    url: 'https://uk.bodybuilding.com/store/opt/whey.html?skuId=OPTEU1370446',
                    image: 'https://store.bbcomcdn.com/images/store/skuimage/sku_OPTEU1370446/image_skuOPTEU1370446_largeImage_X_130_white.jpg',
                    country: 'uk',
                    sku: "OPTEU1370446"
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Optimum Nutrition Gold Standard 100% Whey, 1.09 Kilograms';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '25.99',
                    url: 'https://uk.bodybuilding.com/store/opt/whey.html?skuId=OPTEU5130030',
                    image: 'https://store.bbcomcdn.com/images/store/skuimage/sku_OPTEU5130032/image_skuOPTEU5130032_largeImage_X_130_white.jpg',
                    country: 'uk',
                    sku: "OPTEU5130030"
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
