    var should = require('should');
    var Structure = require('../lib/structure.js')
    const cache = require('../lib/cache')

    describe('supplierTest', function () {

        describe('Search Page', function () {

            var payload = {
                "pageType": "search",
                "url": "https://uk.russellhobbs.com/products/toasters/4-slice-toasters",
                "html": cache.load("https://uk.russellhobbs.com/products/toasters/4-slice-toasters")
            }
            var x = 33
            it('should return' + x + ' number of products', function () {

                const searchPage = Structure(payload)
             //   console.log(searchPage)
                searchPage.length.should.equal(x)


                var first = searchPage.shift()
                first.name.should.be.equal("Brushed Stainless Steel 4 Slice Toaster (24094)");
                first.price.should.be.equal("34.49");
                first.url.should.be.equal("https://uk.russellhobbs.com/product/brushed-stainless-steel-toaster-4-slice");
                first.image.should.be.equal("https://cdn-img.russellhobbs.com/manager/russellhobbs_com/products/uk/small_Brushed_4Slice.png")
                first.country.should.be.equal('us');

                var last = searchPage.pop()

                last.name.should.be.equal("Futura 4 Slice (18790)");
                last.price.should.be.equal("29.99");
                last.url.should.be.equal("https://uk.russellhobbs.com/product/futura-4-slice-18790");
                last.image.should.be.equal("https://cdn-img.russellhobbs.com/manager/russellhobbs_com/products/uk/small_RH_REDESIGN_main_product_imgs_uk_18790.png")
                last.country.should.be.equal('us');


            });

        });

        describe('Out Of Stock Page', function () {

            var payload = {
                "pageType": "product",
                "url": "https://uk.russellhobbs.com/product/4-slice-wide-slot-toaster-22370",
                "html": cache.load("https://uk.russellhobbs.com/product/4-slice-wide-slot-toaster-22370")
            }

            it('should be true when product is in stock', function () {

                const inStockPage = Structure(payload)
              //   console.log(inStockPage)
                inStockPage.name.should.be.equal("4 Slice Wide Slot Toaster");
                inStockPage.price.should.be.equal("64.99");
                inStockPage.url.should.be.equal(payload.url);
                inStockPage.inStock.should.be.equal(false);
                should(inStockPage.stockLevel).be.equal(null);
            //    inStockPage.image.should.be.equal('https://assets.bbystatic.com/header-footer/images/bby_logo-a7e90594729ed2e119331378def6c97e.png');
                inStockPage.country.should.be.equal('us');
            });

        });

        describe('In Stock Page', function () {

            var payload = {
                "pageType": "product",
                "url": "https://uk.russellhobbs.com/product/buckingham-stainless-steel-coffee-maker-20680",
                "html": cache.load("https://uk.russellhobbs.com/product/buckingham-stainless-steel-coffee-maker-20680")
            }

            it('should return false when product is out of stock', function () {

                const inStockPage = Structure(payload)
                inStockPage.name.should.be.equal("Buckingham Stainless Steel Coffee Maker");
                inStockPage.price.should.be.equal('54.99');
                inStockPage.url.should.be.equal(payload.url);
                should(inStockPage.stockLevel).be.equal(null);
                inStockPage.inStock.should.be.equal(true);
            //    inStockPage.image.should.be.equal('https://assets.bbystatic.com/header-footer/images/bby_logo-a7e90594729ed2e119331378def6c97e.png');
                inStockPage.country.should.be.equal('us')
            });

        });


        
        describe('In Store Only Page', function () {

            var payload = {
                "pageType": "product",
                "url": "https://www.bestbuy.com/site/toshiba-43-class-led-2160p-smart-4k-uhd-tv-with-hdr-fire-tv-edition/6194907.p?skuId=6194907",
                "html": cache.load("https://www.bestbuy.com/site/toshiba-43-class-led-2160p-smart-4k-uhd-tv-with-hdr-fire-tv-edition/6194907.p?skuId=6194907")
            }

            it('should return false when product is out of stock', function () {

                const inStockPage = Structure(payload)
            //    console.log(inStockPage)
                inStockPage.name.should.be.equal("Toshiba - 43” Class – LED - 2160p – Smart - 4K UHD TV with HDR – Fire TV Edition");
                inStockPage.price.should.be.equal('129.99');
                inStockPage.url.should.be.equal(payload.url);
                should(inStockPage.stockLevel).be.equal(null);
                inStockPage.inStock.should.be.equal(false);
                inStockPage.image.should.be.equal('https://assets.bbystatic.com/header-footer/images/bby_logo-a7e90594729ed2e119331378def6c97e.png');
                inStockPage.country.should.be.equal('us')
            });

        });


    });