    //                 "country": ''
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://us.camelcamelcamel.com/search?sq=games"
        }

        // Change to be the number of products on the page
        var x = 10;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Hasbro Speak Out Game';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: '0642213213527',
                    name: firstProductName,
                    url: 'https://camelcamelcamel.com/Hasbro-C2018000-Speak-Out-Game/product/B01HH0NTDA?context=browse',
                    image: 'https://images-na.ssl-images-amazon.com/images/I/5182rwxK8gL._SL75_.jpg',
                    asin: 'B01HH0NTDA',
                    breadcrumb: 'Toy » Hasbro',
                    model: 'C2018000',
                    amazonPrice: '16.50',
                    thirdPartyNew: '14.00',
                    country: "us"

                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Bounce-Off Game';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: '6858673019980',
                    name: lastProductName,
                    url: 'https://camelcamelcamel.com/Mattel-CBJ83-Bounce-Off-Game/product/B00IQ8MVH8?context=browse',
                    image: 'https://images-na.ssl-images-amazon.com/images/I/41dlCNRTSIL._SL75_.jpg',
                    asin: 'B00IQ8MVH8',
                    breadcrumb: 'Toy » Mattel',
                    model: 'CBJ83',
                    amazonPrice: '8.45',
                    thirdPartyNew: '4.99',
                    country: "us"
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Check To See If No Match', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://us.camelcamelcamel.com/products?sq=Jazzs+Convertible+%26+Music+Stage"
        }

        // Change to be the number of products on the page
        var x = 0;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

    });

    describe('a different type of search page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://us.camelcamelcamel.com/Barbie-Glitz-Doll-Pink-Dress/product/B0161CFMOY"
        }

        // Change to be the number of products on the page
        var x = 1;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Barbie Glitz Doll, Pink Dress';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: '',
                    name: firstProductName,
                    url: 'https://camelcamelcamel.com/product/1BY0T1M6L15C8FUMXOFYV/go?context=product&ctx_pid=37995718&ctx_cid=-1&ctx_aid=-1&ctx_act=buy&utm_campaign=amazon_products&utm_source=camel-thumb-img&utm_medium=www',
                    image: 'https://images-na.ssl-images-amazon.com/images/I/31JmjMw9XeL._SL160_.jpg',
                    asin: 'B0161CFMOY',
                    breadcrumb: '',
                    model: 'NO MODEL',
                    amazonPrice: '8.95',
                    thirdPartyNew: '',
                    country: "us"

                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });


