var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.4sgm.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.4sgm.com/is-bin/INTERSHOP.enfinity/WFS/4sgm-Storefront-Site/en_US/-/USD/ViewProductDetail-StartRedirected;pgid=MfCCiKaqqOBSR01stzfChHHq0000G26uIfdI;sid=AaofiUo70Y8eiRG2ZTwQi0oxpHd_VIw2VE0=?CatalogCategoryID=jPTAwGQTP9MAAAELGpQ0E4U1&ProductUUID=VXQKAAIKs28AAAFlRjQoaQQX",
            "html": cache.load("http://www.4sgm.com/is-bin/INTERSHOP.enfinity/WFS/4sgm-Storefront-Site/en_US/-/USD/ViewProductDetail-StartRedirected;pgid=MfCCiKaqqOBSR01stzfChHHq0000G26uIfdI;sid=AaofiUo70Y8eiRG2ZTwQi0oxpHd_VIw2VE0=?CatalogCategoryID=jPTAwGQTP9MAAAELGpQ0E4U1&ProductUUID=VXQKAAIKs28AAAFlRjQoaQQX")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Cutie Pie Blue Faux Leather Baby Shoes",
                    "price": null,
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.4sgm.com/is-bin/intershop.static/WFS/4sgm-Storefront-Site/-/en_US/images/product_new/S/34300_S.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.4sgm.com/is-bin/INTERSHOP.enfinity/WFS/4sgm-Storefront-Site/en_US/-/USD/ViewProductDetail-StartRedirected;pgid=MfCCiKaqqOBSR01stzfChHHq0000G26uIfdI;sid=AaofiUo70Y8eiRG2ZTwQi0oxpHd_VIw2VE0=?CatalogCategoryID=oHTAwGQTfE8AAAELv4U0E4U1&ProductUUID=UXIKAAIK.fEAAAESICRdMXig",
            "html": cache.load("http://www.4sgm.com/is-bin/INTERSHOP.enfinity/WFS/4sgm-Storefront-Site/en_US/-/USD/ViewProductDetail-StartRedirected;pgid=MfCCiKaqqOBSR01stzfChHHq0000G26uIfdI;sid=AaofiUo70Y8eiRG2ZTwQi0oxpHd_VIw2VE0=?CatalogCategoryID=oHTAwGQTfE8AAAELv4U0E4U1&ProductUUID=UXIKAAIK.fEAAAESICRdMXig")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Threshold Decorative Drapery Rod - 66-120\"",
                    "price": null,
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://www.4sgm.com/is-bin/intershop.static/WFS/4sgm-Storefront-Site/-/en_US/images/product_new/S/26929_S.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "http://www.4sgm.com/is-bin/INTERSHOP.enfinity/WFS/4sgm-Storefront-Site/en_US/-/USD/ViewParametricSearch-AdvancedSearch;pgid=MfCCiKaqqOBSR01stzfChHHq0000G26uIfdI?SearchCategoryUUID=pyHAwGQTy_8AAAELa900E4U1&nav=true&SearchKey=ViewAll",
        "html": cache.load("http://www.4sgm.com/is-bin/INTERSHOP.enfinity/WFS/4sgm-Storefront-Site/en_US/-/USD/ViewParametricSearch-AdvancedSearch;pgid=MfCCiKaqqOBSR01stzfChHHq0000G26uIfdI?SearchCategoryUUID=pyHAwGQTy_8AAAELa900E4U1&nav=true&SearchKey=ViewAll")
    }

    // Change to be the number of products on the page
    var x = 12;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'SPANISH MINI BABY ANIMALS BOARD BOOKS';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '0.65',
                url: 'http://www.4sgm.com/is-bin/INTERSHOP.enfinity/WFS/4sgm-Storefront-Site/en_US/-/USD/ViewProductDetail-StartSavingParameters;pgid=MfCCiKaqqOBSR01stzfChHHq0000G26uIfdI?ProductUUID=0vAKAAIOcGEAAAFl_eQvUSLh&CatalogCategoryID=K5MKAAIMpjgAAAEU0tUm_YP1&JumpTo=OfferList',
                image: 'http://www.4sgm.com/is-bin/intershop.static/WFS/4sgm-Storefront-Site/-/en_US/images/product_new/S/30197_S.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = '2pc 0-12mon PINK/PURPLE HEADBANDwFLOWER APPLIQUE';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '0.65',
                url: 'http://www.4sgm.com/is-bin/INTERSHOP.enfinity/WFS/4sgm-Storefront-Site/en_US/-/USD/ViewProductDetail-StartSavingParameters;pgid=MfCCiKaqqOBSR01stzfChHHq0000G26uIfdI?ProductUUID=7m4KAAIKmE8AAAEYzFsnKo2u&CatalogCategoryID=K5MKAAIMpjgAAAEU0tUm_YP1&JumpTo=OfferList',
                image: 'http://www.4sgm.com/is-bin/intershop.static/WFS/4sgm-Storefront-Site/-/en_US/images/product_new/S/33843_S.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.4sgm.com/is-bin/INTERSHOP.enfinity/WFS/4sgm-Storefront-Site/en_US/-/USD/ViewParametricSearch-AdvancedSearch;pgid=MfCCiKaqqOBSR01stzfChHHq0000G26uIfdI?SearchCategoryUUID=pyHAwGQTy_8AAAELa900E4U1&nav=true&SearchKey=ViewAll",
            "html": cache.load("http://www.4sgm.com/is-bin/INTERSHOP.enfinity/WFS/4sgm-Storefront-Site/en_US/-/USD/ViewParametricSearch-AdvancedSearch;pgid=MfCCiKaqqOBSR01stzfChHHq0000G26uIfdI?SearchCategoryUUID=pyHAwGQTy_8AAAELa900E4U1&nav=true&SearchKey=ViewAll")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Books",
                    "url": payload.url, 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


