var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.511tactical.com/tac-a2-gloves.html",
            "html": cache.load("https://www.fabric.com/buy/0260771/faux-fur-hi-low-rabbit-rust-brown")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "TAC A2 Gloves",
                    "price": "29.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.511tactical.com/media/catalog/product/cache/1/image/560x/9df78eab33525d08d6e5fb8d27136e95/59340/019/59340_019_01.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.511tactical.com/footwear.html",
            "html": cache.load("http://www.511tactical.com/footwear.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 48,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.511tactical.com/accessories/gloves.html",
            "html": cache.load("http://www.511tactical.com/accessories/gloves.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Tactical Gloves"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.511tactical.com/womens/footwear/boots.html",
            "html": cache.load("https://www.511tactical.com/womens/footwear/boots.html")
        }

        // Change to be the number of products on the page
        var x = 4;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Rapid Quad Zip Pack';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '89.99',
                    url: 'http://www.511tactical.com/rapid-quad-zip-pack.html',
                    image: 'http://www.511tactical.com/media/catalog/product/cache/1/small_image/220x/9df78eab33525d08d6e5fb8d27136e95/56371/926/56371_926_01.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })
        var lastProductName = 'Mira 2-in-1 Pack';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '129.99',
                    url: 'http://www.511tactical.com/mira-2-in-1.html',
                    image: 'http://www.511tactical.com/media/catalog/product/cache/1/small_image/220x/9df78eab33525d08d6e5fb8d27136e95/56338/146/56338_146_01.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
