var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.7dayshop.com/products/sumvision-rio-2-4ghz-wireless-mini-multi-media-keyboard-with-built-in-track-ball-for-xbmc-android-tv-box-pc-etc-kb-w-24g-mini",
            "html": cache.load("https://www.7dayshop.com/products/sumvision-rio-2-4ghz-wireless-mini-multi-media-keyboard-with-built-in-track-ball-for-xbmc-android-tv-box-pc-etc-kb-w-24g-mini")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Sumvision Rio 2.4GHz Wireless Mini Multi-Media Keyboard with Built-in Track Ball for XBMC Android TV BOX PC etc.",
                    "price": "19.49",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.7dayshop.com/skin/frontend/smartwave/porto/images/7DS_2016_logo_243_25.png",
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "",
            "html": cache.load("")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": ""
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.7dayshop.com/computer-keyboards-mice",
            "html": cache.load("https://www.7dayshop.com/computer-keyboards-mice")
        }

        // Change to be the number of products on the page
        var x = 22;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Marley Pug Pop Over Phone Case';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.80',
                url: 'https://www.claires.com/marley-pug-pop-over-phone-case-229291M.html?cgid=168&lang=en_GB',
                image: 'https://www.claires.com/dw/image/v2/BBTK_PRD/on/demandware.static/-/Sites-master-catalog/default/dw3b322a6b/images/hi-res/98823_1.jpg?sw=290&sh=290&sm=fit',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Rainbow Marble with Gold Foil Flakes Phone Case';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '4.80',
                url: 'https://www.claires.com/rainbow-marble-with-gold-foil-flakes-phone-case-230461.html?cgid=168&lang=en_GB',
                image: 'https://www.claires.com/dw/image/v2/BBTK_PRD/on/demandware.static/-/Sites-master-catalog/default/dw7ad4ce93/images/hi-res/23509_1.jpg?sw=290&sh=290&sm=fit',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    }); 

});


