var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.a4c.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.a4c.com/product/2-pack-crystal-clarity-his-hers-set-for-iphone-5-5s-pink-blue-case-mini-stylus-screen-protector.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "2 Pack Crystal Clarity His & Hers Set for iPhone 5 / 5S : Pink & Blue Case + Mini Stylus + Screen Protector",
                    "price": "3.49",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://cdn.a4c.com/media/catalog/product/cache/1/image/360x360/9df78eab33525d08d6e5fb8d27136e95/2/-/2-pack-crystal-clarity-his-_-hers-set-for-iphone-5-pink-_-blue-case-_-mini-stylus-_-screen-protector-main-view.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.a4c.com/product/at-t-ifrogz-captivate-glide-screen-protector-kit-2-pack.html"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "AT&T iFrogz Captivate Glide Screen Protector Kit - 2 Pack",
                    "price": "4.95",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://cdn.a4c.com/media/catalog/product/cache/1/image/360x360/9df78eab33525d08d6e5fb8d27136e95/a/t/att-ifrogz-captivate-glide-screen-protector-kit-2-pack-main-view.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.a4c.com/chargers/wall-chargers/charger-types.html?limit=70&"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 70,
                    "pages": 12
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.a4c.com/screen-protectors.html"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Screen Protectors"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.a4c.com/cases/otterbox/manufacture.html"
        }

        // Change to be the number of products on the page
        var x = 16;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'OtterBox iPhone 4S Defender Series Holster - Black (Refurbished)';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '2.95',
                    url: 'https://www.a4c.com/product/otterbox-iphone-4s-defender-series-holster-black.html',
                    image: 'https://cdn.a4c.com/media/catalog/product/cache/1/small_image/204x/9df78eab33525d08d6e5fb8d27136e95/o/t/otterbox-iphone-4s-defender-series-holster-black-main-view.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'OtterBox iPhone 6 Plus / 6S Plus Defender Series Case & Holster (Refurbished)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '19.95',
                    url: 'https://www.a4c.com/product/otterbox-iphone-6-plus-6s-plus-defender-series-case-holster-refurbished.html',
                    image: 'https://cdn.a4c.com/media/catalog/product/cache/1/small_image/204x/9df78eab33525d08d6e5fb8d27136e95/o/t/otterbox-iphone-6-plus-6s-plus-defender-series-case-and-holster-view-of-color-options-thumb_3.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
