var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.aardstore.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.aardstore.com/*/Toys/Shivering-Shaun-30cm-Plush/2HW101AX000"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Shivering Shaun 30cm Plush",
                    "price": "17.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://dhgkpqsiufwl2.cloudfront.net/media/ArticleSharedImage/image300/.fqhGplLV/ArticleSharedImage-60592.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.aardstore.com/*/Toys/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Toys"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.aardstore.com/*/Toys/Magnetic-Mini-Plush-Timmy-12cm/2IP301AX000"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Magnetic Mini Plush Timmy 12cm",
                    "price": "6.49",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://dhgkpqsiufwl2.cloudfront.net/media/ArticleSharedImage/image300/.f5VXv6qU/ArticleSharedImage-45606.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.aardstore.com/40th-Anniversary/"
        }

        // Change to be the number of products on the page
        var x = 28;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Micro Star Trading R / C Jumbo BB8';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '23.90',
                    url: 'https://www.alza.co.uk/toys/mikro-trading-star-wars-r-c-jumbo-bb8-d4529469.htm',
                    image: 'https://i.alza.cz/Foto/f8/HR/HRAa10227.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'LEGO DUPLO 10804 Jungle';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '32.90',
                    url: 'https://www.alza.co.uk/toys/lego-duplo-10804-jungle-d4634412.htm',
                    image: 'https://i.alza.cz/Foto/f8/EU/EULO10804.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
