var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.abesofmaine.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.abesofmaine.com/Samsung_HW-J550_Sound_Bar_System_-_2_1_Channel_-_320W_RMS_Wireless_1013767.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Samsung HW-J550 Sound Bar System - 2.1 Channel - 320W RMS Wireless",
                    "price": "339.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.abesofmaine.com/files/AB/product/medium/samsung_hw-j550_sound_bar_system_-_2_1_channel_-_320w_rms_-_wireless_1013767.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.abesofmaine.com/Store/TV_s_Entertaiment/LED_OLED_Televisions/?Page=1"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 30,
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.abesofmaine.com/HLP48HDSSS_48_Wall_Mount_Vent_Hood_-_Stainless_Steel_1004194.html"
        }

        it('should be true when number of items and pages are null', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",

            "url" : "http://www.abesofmaine.com/Store/TV_s_Entertaiment/Speakers/"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Speakers"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.abesofmaine.com/Store/TV_s_Entertaiment/LED_OLED_Televisions/?Page=1"
        }

        // Change to be the number of products on the page
        var x = 30;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'LG OLED65B6P 65-inch Smart 4K UHD OLED TV';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '2199.00',
                    url: 'http://www.abesofmaine.com/LG_OLED65B6P_65-inch_Smart_4K_UHD_OLED_TV_1029964.html',
                    image: 'http://www.abesofmaine.com/files/AB/product/medium/lg_signature_b6_series_oled65b6p_-_65_oled_smart_tv_-_4k_ultrahd_1029964.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Samsung 43KU6300 Series 43" Class UHD Smart LED TV';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '645.00',
                    url: 'http://www.abesofmaine.com/Samsung_43KU6300_Series_43_Class_UHD_Smart_LED_TV_1029250.html',
                    image: 'http://www.abesofmaine.com/files/AB/product/medium/samsung_43ku6300-series_43_-class_uhd_smart_led_tv_1029250.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
