var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.abt.com/product/77873/Tumi-Alpha-Black-Money-Clip-Card-Case-0119251DID.html",
            "html": cache.load("https://www.abt.com/product/77873/Tumi-Alpha-Black-Money-Clip-Card-Case-0119251DID.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Tumi Alpha Black Money Clip Card Case - 0119251D-ID",
                    "price": "48.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://content.abt.com/image.php/big_0119251DID.jpg?image=/images/products/BDP_Images/big_0119251DID.jpg&canvas=1&quality=80&width=60&height=55",
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "",
            "html": cache.load("")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": ""
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.abt.com/category/1159/Mens-Wallets.html",
            "html": cache.load("https://www.abt.com/category/1159/Mens-Wallets.html")
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Tumi Alpha Black Money Clip Card Case - 0119251D-ID';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '48.00',
                    url: 'https://www.abt.com/product/77873/Tumi-Alpha-Black-Money-Clip-Card-Case-0119251DID.html',
                    image: 'https://content.abt.com/image.php/p77873.jpg?width=150&height=110&canvas&image=/images/products/BDP_Images/big_0119251DID.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Tumi Nassau Black Textured ID Lock Global Removable Passcase - 0186135D';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '132.00',
                    url: 'https://www.abt.com/product/113840/Tumi-Nassau-Black-Textured-ID-Lock-Global-Removable-Passcase-0186135D.html',
                    image: 'https://content.abt.com/image.php/p113840.jpg?width=150&height=110&canvas&image=/images/products/BDP_Images/big_0186135D.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


